import pandas as pdimport numpy as npimport matplotlib.pyplot as pltdataset = pd.read_csv("kalite-fiyat.csv")x = dataset.iloc[:, :-1].valuesy = dataset.iloc[:, -1].values#Decision Tree Modelinin Eğitilmesifrom sklearn.tree import DecisionTreeRegressorregressor = DecisionTreeRegressor(random_state = 0)regressor.fit(x, y)#Decision Tree Modeli Tahmin Denemesiy_predict = regressor.predict([[6.5]])#Decision Tree Modelinin Görselleştirilmesix_grid = np.arange(min(x), max(x), 0.1)x_grid = x_grid.reshape((len(x_grid), 1)) # satır ve sütunların yerini değiştirir (dikey hale getirir)plt.scatter(x, y, color = "red")plt.plot(x_grid, regressor.predict(x_grid), color = "blue")plt.title("Decision Tree Modeli")plt.xlabel("Kalite")plt.ylabel("Fiyat")plt.show()